const transformMeeting = (meeting) => {
  const { meetingId, meetingType, meetingDate, aiResponse } = meeting;

  // Defensive check for aiResponse
  if (!aiResponse || typeof aiResponse !== 'string') {
    console.log('Missing or invalid aiResponse:', { meetingId, aiResponse: typeof aiResponse });
    return createEmptyResult(meetingId, meetingType, meetingDate);
  }

  // Parse structured sections from aiResponse
  const parseSection = (text, sectionNum, sectionName) => {
    if (!text) return '';
    const regex = new RegExp(`\\*\\*${sectionNum}\\. ${sectionName}:\\*\\*\\s*([\\s\\S]*?)(?=\\*\\*\\d+\\.|$)`, 'i');
    const match = text.match(regex);
    return match ? match[1].trim() : '';
  };

  // Extract sections
  const actionItemsText = parseSection(aiResponse, '6', 'ACTION ITEMS');
  const decisionsText = parseSection(aiResponse, '5', 'KEY DECISIONS');  
  const commitmentsText = parseSection(aiResponse, '3', 'COMMITMENTS MADE');
  const summaryText = parseSection(aiResponse, '4', 'MEETING SUMMARY');
  const requirementsText = parseSection(aiResponse, '1', 'CLIENT REQUIREMENTS');

  // Parse action items
  const actionItems = actionItemsText ? actionItemsText.split('\n')
    .filter(line => line.trim().startsWith('-'))
    .map(line => {
      const parts = line.substring(1).split('|').map(p => p.trim());
      return {
        description: parts[0] || '',
        owner: parts.find(p => p.startsWith('Owner:'))?.replace('Owner:', '').trim() || 'Not assigned',
        dueDate: parts.find(p => p.startsWith('Due:'))?.replace('Due:', '').trim() || '',
        priority: parts.find(p => p.startsWith('Priority:'))?.replace('Priority:', '').trim() || 'Medium'
      };
    }) : [];

  // Parse decisions
  const decisions = decisionsText ? decisionsText.split('\n')
    .filter(line => line.trim().startsWith('-'))
    .map(line => {
      const parts = line.substring(1).split('|').map(p => p.trim());
      return {
        decision: parts[0] || '',
        madeBy: parts.find(p => p.startsWith('Made by:'))?.replace('Made by:', '').trim() || 'Unknown',
        impact: parts.find(p => p.startsWith('Impact:'))?.replace('Impact:', '').trim() || ''
      };
    }) : [];

  // Parse commitments  
  const commitments = commitmentsText ? commitmentsText.split('\n')
    .filter(line => line.trim().startsWith('-'))
    .map(line => {
      const parts = line.substring(1).split('|').map(p => p.trim());
      return {
        commitment: parts[0] || '',
        responsible: parts.find(p => p.startsWith('Responsible:'))?.replace('Responsible:', '').trim() || 'Unassigned',
        deadline: parts.find(p => p.startsWith('Deadline:'))?.replace('Deadline:', '').trim() || '',
        deliverable: parts.find(p => p.startsWith('Deliverable:'))?.replace('Deliverable:', '').trim() || ''
      };
    }) : [];

  return {
    success: true,
    actionItems,
    decisions,
    risks: [], // No risks in source data
    commitments,
    summaryRecord: {
      meetingDate,
      meetingType,
      keyDecisions: decisions.length ? decisions.map(d => d.decision).join('; ') : 'No major decisions',
      executiveSummary: summaryText || 'Summary not available',
      nextSteps: actionItems.length ? actionItems.map(ai => ai.description).join('; ') : 'No action items',
      attendees: 'Not specified',
      meetingId
    },
    processingMetrics: {
      actionItemsCount: actionItems.length,
      decisionsCount: decisions.length,
      risksCount: 0,
      commitmentsCount: commitments.length,
      processingTime: new Date().toISOString(),
      meetingType,
      meetingId
    },
    fullAnalysis: aiResponse,
    meetingId,
    meetingType,
    meetingDate
  };
};

