  // Check if we have a file upload in binary data
  const hasBinary = item.binary && Object.keys(item.binary).length > 0;
  let binaryData = null;
  let binaryKey = null;

  if (hasBinary) {
    // Get the first binary key (usually 'file' from Streamlit)
    binaryKey = Object.keys(item.binary)[0];
    binaryData = item.binary[binaryKey];
    console.log('Binary key:', binaryKey);
    console.log('Binary data:', binaryData);
  }

  // Determine file type and prepare data
  let fileType = 'text';
  let fileName = 'Direct Input';
  let mimeType = '';
  let fileContent = '';
  let needsTranscription = false;

  if (binaryData) {
    fileName = binaryData.fileName || 'Unknown File';
    mimeType = binaryData.mimeType || '';

    // Check if it's audio
    if (mimeType.includes('audio') || 
        fileName.toLowerCase().endsWith('.mp3') || 
        fileName.toLowerCase().endsWith('.wav') ||
        fileName.toLowerCase().endsWith('.m4a')) {
      fileType = 'audio';
      needsTranscription = true;
    } else if (mimeType.includes('text') || fileName.toLowerCase().endsWith('.txt')) {
      fileType = 'text';
      // Extract text content from binary
      try {
        const buffer = Buffer.from(binaryData.data, 'base64');
        fileContent = buffer.toString('utf-8');
        console.log('Extracted text content:', fileContent.substring(0, 100));
      } catch (error) {
        console.error('Error reading text file:', error);
        fileContent = 'Error reading file content';
      }
    }
  }

  // Get meeting metadata from form data
  const meetingType = item.json.meetingType || 'general';
  const attendees = item.json.attendees || 'Unknown';
  const meetingNotes = fileContent || item.json.meetingNotes || '';

  console.log('Processed data:', {
    fileType,
    fileName,
    needsTranscription,
    meetingType,
    hasContent: meetingNotes.length > 0
  });

  // Create output item
  const outputItem = {
    json: {
      fileType: fileType,
      fileName: fileName,
      mimeType: mimeType,
      meetingType: meetingType,
      meetingNotes: meetingNotes,
      attendees: attendees,
      needsTranscription: needsTranscription,
      timestamp: new Date().toISOString(),
      meetingId: `meeting-${Date.now()}`,
      binaryKey: binaryKey || 'data'
    }
  };

  // Preserve binary data if it exists
  if (hasBinary) {
    outputItem.binary = item.binary;
  }

  items.push(outputItem);
}

return items;

