// Enhanced classification logic based on content analysis
function classifyMeetingType(transcript) {
  const content = (transcript).toLowerCase();

  // Scoring system for better accuracy
  const typeScores = {
    standup: 0,
    strategy: 0,
    client: 0,
    general: 0
  };

  // Standup indicators (higher weight for specific phrases)
  const standupPatterns = [
    { pattern: /what did you do yesterday|what are you working on today|any blockers/g, weight: 10 },
    { pattern: /standup|daily|scrum|sprint/g, weight: 8 },
    { pattern: /yesterday|today|tomorrow|blocked|blocker/g, weight: 3 },
    { pattern: /completed|working on|will do|impediment/g, weight: 2 }
  ];

  // Strategy indicators
  const strategyPatterns = [
    { pattern: /roadmap|strategy|vision|goal|objective|initiative/g, weight: 8 },
    { pattern: /quarterly|annual|long.?term|strategic|planning/g, weight: 6 },
    { pattern: /budget|investment|resource allocation|priorit/g, weight: 4 },
    { pattern: /market|competitive|business model/g, weight: 3 }
  ];

  // Client indicators
  const clientPatterns = [
    { pattern: /client|customer|proposal|requirement|delivery/g, weight: 8 },
    { pattern: /contract|agreement|scope|timeline|budget/g, weight: 6 },
    { pattern: /feedback|review|approval|sign.?off/g, weight: 4 },
    { pattern: /external|stakeholder|presentation/g, weight: 3 }
  ];

  // Calculate scores
  for (const patterns of [standupPatterns, strategyPatterns, clientPatterns]) {
    const typeKey = patterns === standupPatterns ? 'standup' : 
                   patterns === strategyPatterns ? 'strategy' : 'client';

    patterns.forEach(({ pattern, weight }) => {
      const matches = content.match(pattern) || [];
      typeScores[typeKey] += matches.length * weight;
    });
  }

  // Determine winning type (minimum threshold to avoid false positives)
  const minThreshold = 5;
  const maxScore = Math.max(...Object.values(typeScores));
  const winningType = maxScore >= minThreshold ? 
    Object.keys(typeScores).find(key => typeScores[key] === maxScore) : 'general';

  return {
    detectedType: winningType,
    confidence: Math.min(maxScore / 20, 1), // Normalised confidence score
    scores: typeScores,
    reasoning: `Classified as ${winningType} with ${Math.round(maxScore)} points`
  };
}

